import React from 'react';
import Link from 'next/link';

import './Menu.css';


const Menu = () => {
  return (
    <div className="top">
        <div className="menubox">
          <Link href="/contents" className="menu_a" activeClassName="active">
              <a>전체글</a>
          </Link>            
          <Link href="/member" className="menu_m" activeClassName="active">
              멤버
          </Link>            
        </div>
      </div>
  );
};

export default Menu;


