//     };

// export const loginRequestAction = data=> ({
//     type : LOG_IN_REQUEST,
//     data,
// });
// export const logoutRequestAction = {
//     type : LOG_OUT_REQUEST,
// };
///////////////////////////////////////
export default (state = initialState,action) => {
  switch (action.type){
      case LOG_IN_REQUEST:{
          return {
              ...state,
              isLoading: true,
              logInErrorReason: '',
          };
      }
      case LOG_IN_SUCCESS : {
          return {
            ...state,
            isLoggingIn : false,
            me : action.data,
            isLoading: false,
          };
      }
      case LOG_IN_FAILURE : {
        return {
          ...state,
          isLoggingIn : false,
          logInErrorReason : action.error,
          me : null,
        };
    }
      case LOG_OUT_REQUEST : {
        return {
            ...state,
            isLoggingOut: true,
        };
      }
      case LOG_OUT_SUCCESS : {
        return {
            ...state,
            isLoggingOut: false,
            me : null,
        };
      }
      case LOG_OUT_FAILURE : {
        return {
            ...state,
            isLoggingOut: false,
            logInErrorReason : action.error,
            me : dummyUser,
        };
      }

      case SIGN_UP_REQUEST : {
        return {
            ...state,
            isSigningUp: true,
            isSignedUp: false,
            signUpErrorReason: '',
        };
      }
      case SIGN_UP_SUCCESS : {
        return {
            ...state,
            isSigningUp: false,
            isSignedUp: true,
        };
      }
      case SIGN_UP_FAILURE : {
        return {
            ...state,
            isSigningUp: false,
            signUpErrorReason: action.error,
        };
      }

      case LOAD_USER_REQUEST: {
        return {
          ...state,
        };
      }
      case LOAD_USER_SUCCESS: {
        return {
          ...state,
          me: action.data,
        };
      }
      case LOAD_USER_FAILURE: {
        return {
          ...state,
        };
      }
      default: {
          return{
              ...state,
          };
      }
  }  
};
