import React, { Component } from 'react';

class Toggle extends Component {
  state = {
    on : false,
  }

  toggle=()=>{
    this.ListeningStateChangedEvent({
      on: !this.state.on
    })
  }
  
  render() {
    return (
      <div>
         {this. state.on&& <h1>토글</h1> }
          <button onClick={this.toggle}>토글버튼</button>
      </div>
    );
  }
}

export default Toggle;
